// Native modules import
import React, { Component } from "react";

class Signup extends Component {
  constructor() {
    super();
    this.state = {
      pseudo: "",
      email: "",
      password: "",
      error: "",
      success: false,
    };
  }

  handleChange = (name) => (event) => {
    this.setState({
      error: "",
    });
    this.setState({
      [name]: event.target.value,
    });
  };

  signupSubmit = (event) => {
    event.preventDefault();
    const { pseudo, email, password } = this.state;
    const newUser = { pseudo, email, password };
    this.signup(newUser).then((res) => {
      if (res.error) {
        this.setState({
          error: res.error,
        });
      } else {
        this.setState({
          pseudo: "",
          email: "",
          password: "",
          error: "",
          success: true,
        });
      }
    });
  };

  signup = async (newUser) => {
    try {
      const res = await fetch("http://localhost:9092/signup", {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newUser),
      });
      return await res.json();
    } catch (err) {
      console.log(err);
    }
  };

  render() {
    const { pseudo, email, password, error, success } = this.state;
    return (
      <div className="container">
        <h2 className="mt-5 mb-5">Inscription</h2>

        <div
          className="alert alert-warning"
          style={{ display: error ? "" : "none" }}
        >
          {error}
        </div>
        <div
          className="alert alert-success"
          style={{ display: success ? "" : "none" }}
        >
          Ton compte vient d'être créé, pirate ! A la Taverne, maintenant !
        </div>
        <form action="">
          <div className="form-group">
            <label htmlFor="Name" className="text-muted">
              Pseudo
            </label>
            <input
              onChange={this.handleChange("pseudo")}
              type="text"
              className="form-control"
              value={pseudo}
              placeholder="Le nom qui sera visible par les autres utilisateurs de la Taverne"
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="Email" className="text-muted">
              E-mail
            </label>
            <input
              onChange={this.handleChange("email")}
              type="email"
              className="form-control"
              value={email}
              placeholder="Votre e-mail de contact (example : guy-tard@gmail.com)"
              required
            />
            <small id="emailHelp" className="form-text text-muted">
              Votre e-mail ne sera partagé avec aucune tierse partie
            </small>
          </div>
          <div className="form-group">
            <label htmlFor="Password" className="text-muted">
              Mot de passe
            </label>
            <input
              onChange={this.handleChange("password")}
              type="password"
              className="form-control"
              value={password}
              placeholder="Ne communiquez jamais votre mot de passe à qui que ce soit."
              required
            />
          </div>
          <button
            onClick={this.signupSubmit}
            type="submit"
            className="btn btn-raised btn-primary"
          >
            S'inscrire
          </button>
        </form>
      </div>
    );
  }
}

export default Signup;
