// Native modules import
import React, { Component } from "react";

// Personal modules import
import { listAllUsers } from "./apiUser";

class Users extends Component {
  constructor() {
    super();
    this.state = {
      users: [],
    };
  }

  componentDidMount = async () => {
    let allUsers;
    try {
      allUsers = await listAllUsers();
    } catch (error) {
      console.error(
        `The list of all users couldn't be retrieved because of error: ${error}.`
      );
    }
    if (allUsers) {
      try {
        this.setState({
          users: allUsers,
        });
      } catch (error) {
        console.error(
          `Couldn't change state of users because of error: ${error}.`
        );
      }
    } else {
      console.error(
        `No users were retrieved from the server because of error:${allUsers.error}.`
      );
    }
  };

  render() {
    const {users} = this.state;
    return (
      <div className="jumbotron">
        <h2 className="mt-3 mb-3">Les pirates de la Taverne</h2>

        <div className="card">
          {users.users.map((user, i) => (
            <p>{user.pseudo}</p>
          ))}
        </div>
      </div>
    );
  }
}

export default Users;
