// Native modules import
import React, { Component } from "react";
import { Redirect } from "react-router-dom";

// Personal modules import
import { isAuthenticated } from "../auth/index";
import { read } from "./apiUser";

class Profile extends Component {
  constructor() {
    super();
    this.state = {
      user: "",
      redirectionSignIn: false,
    };
  }

  init = async (userId) => {
    let token;
    try {
      token = isAuthenticated().token;
    } catch (error) {
      console.error(`Couldn't retrieve token because of error: ${error}.`);
    }
    if (token) {
      let data;
      try {
        data = await read(userId, token);
      } catch (error) {
        console.error(`Couldn't retrieve data because of error: ${error}.`);
      }
      if (data) {
        try {
          if (data.error) {
            try {
              this.setState = {
                redirectionSignIn: true,
              };
            } catch (error) {
              console.error(`Couldn't set state of redirectionSignIn.`);
            }
          } else {
            try {
              this.setState({
                user: data,
              });
            } catch (error) {
              console.error(`Couldn't set state or of user.`);
            }
          }
        } catch (error) {
          console.error(
            `No error property inside data object was found because of error: ${error}.`
          );
        }
      } else {
        console.error(`No data object was found.`);
      }
    } else {
      console.error(`Token inside init method is ${typeof token}.`);
    }
  };

  componentDidMount() {
    try {
      const userId = this.props.match.params.userId;
      if (userId) {
        this.init(userId);
      } else {
        console.error("No userId found.");
      }
    } catch (error) {
      console.error("Something went wrong with componentDidMount method.");
    }
  }

  render() {
    const redirectionSignIn = this.state.redirectionSignIn;
    if (redirectionSignIn) {
      return <Redirect to="/signin" />;
    }
    return (
      <div className="container">
        <div className="col-md-6">
          <div className="jumbotron">
            <h2 className="mb-3 mt-3">Planque</h2>
            <p className="mt-2 mb-2 lead">
              Bienvenue dans ta planque, {isAuthenticated().user.pseudo} !
            </p>
            <p>{`Tu as rejoint la Taverne des Soiffards le ${new Date(
              this.state.user.created
            ).toLocaleDateString()}`}</p>
            <p>{`Ton e-mail : ${this.state.user.email}`}</p>
          </div>
        </div>
        <div className="col-md-6">
          Modifier Supprimer
        </div>
      </div>
    );
  }
}

export default Profile;
