// Native modules import
import React, { useEffect, useState } from "react";
// import ReactDOM from "react-dom";
import io from "socket.io-client";

// Personal modules import
import { isLoggedIn } from "./index";

const socket = io(process.env.CLIENT_URI, {
  transports: ["websocket", "polling"],
});

// const pirateName = prompt("Quel est ton nom, pirate ?");

const Home = () => {
  const [users, setUsers] = useState([]);
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    socket.on("connect", () => {
      socket.emit("pirateName", isLoggedIn().user.pseudo);
    });

    socket.on("users", (users) => {
      setUsers(users);
    });

    socket.on("message", (message) => {
      setMessages((messages) => [...messages, message]);
    });

    socket.on("connected", (user) => {
      setUsers((users) => [...users, user]);
    });

    socket.on("disconnect", (id) => {
      setUsers((users) => {
        return users.filter((user) => user.id !== id);
      });
    });
  }, []);

  const submitForm = (event) => {
    event.preventDefault();
    socket.emit("send", message);
    setMessage("");
  };

  return (
    <div className="container jumbotron">
      <div className="row">
        <div className="col-md-12 mt-5 mb-5">
          <h4>Bievenue dans la Taverne, {isLoggedIn().user.pseudo}</h4>
        </div>
      </div>
      <div className="row">
        <div className="col-md-8">
          <h5>Messages</h5>
          <div id="messages">
            {messages.map(({ user, message }, index) => (
              <div key={index} className="row mb-2">
                <div className="col-md-2">{user.pseudo}</div>
                <div className="col-md-2">{message}</div>
              </div>
            ))}
          </div>
          <form action="" id="messageForm" onSubmit={submitForm}>
            <div className="input-group">
              <input
                type="text"
                className="form-control"
                onChange={(event) => setMessage(event.currentTarget.value)}
                value={message}
                id="message"
              />
              <span className="input-group-btn">
                <button className="btn btn-primary btn-sm" id="submitForm">
                  Envoyer
                </button>
              </span>
            </div>
          </form>
        </div>
        <div className="col-md-4">
          <h5>Pirates</h5>
          <ul id="users">
            {users.map(({ name, id }) => (
              <li key={id}>{name}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

