  //     );
  //   }
  //   if (allPosts) {
  //     try {
  //       this.setState({
  //         posts: allPosts,
  //       });
  //     } catch (error) {
  //       console.error(
  //         `Couldn't change state of posts because of error: ${error}.`
  //       );
  //     }
  //   } else {
  //     console.error(
  //       `No posts were retrieved from the server because of error.`
  //     );
  //   }
  // };

  renderPosts = (posts) => {
    
    return (
    <div className="row">
      {posts.map((post, i) => (
        <div
          className="card col-xs-12 col-sm-6 col-md-4 col-lg-3 col-xl-2"
          key={i}
        >
          {/* <img
            style={{ height: "auto", width: "auto" }}
            className="img-thumbnail"
            src={`${process.env.REACT_APP_API_URL}/posts/by/${post.author._id}`}
            // onError={img => (img.target.src = defaultProfilePic)}
            alt={post.author.pseudo}
          />
          <img
            className="card-img-top"
            src={defaultProfilePic}
            style={{ width: "100%", heigth: "15vw", objectFit: "cover" }}
            alt={user.pseudo}
          />  */}
          <div className="card-body" key={i}>
            <h5 className="card-title">{post.title}</h5>
            <p className="card-text">{post.body}</p>
          </div>

          <Link
            to={`/posts/${post._id}`}
            className="btn btn-raised btn-sm"
            style={{ color: "#5E8C5D" }}
          >
            Lire plus
          </Link>
        </div>
      ))}
    </div>
  )
};

  render() {
    const { posts } = this.state;
    return (
      <div className="jumbotron">
        <h2 className="mt-3 mb-3">Les messages de la Taverne</h2>
        {this.renderPosts(posts)}
      </div>
    );
  }
}

export default Posts;
