import React from "react";
import { Link, withRouter } from "react-router-dom";

const isActive = (history, path) => {
  if (history.location.pathname === path) {
    return {
      color: "#5E8C5D",
    };
  } else {
    return {
      color: "#0D0D0D",
    }
  }
};

export const signout = async (redirect) => {
  if (typeof window !== 'undefined') {
    localStorage.removeItem('jwt');
    redirect();
    let response;
    try {
      response = await fetch('http://localhost:9092/signout', {
        method: "GET"
      });
    } catch (error) {
      console.log(error);
    }
    console.log('signout', response);
    return response.json({})
  }
}

const Menu = ({history}) => (
  <div className="bg-primary">
    <nav className="navbar fixed-top navbar-light bg-light">
      {/* <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
        <span className="navbar-toggler-icon"></span>
      </button> */}
      <ul className="nav nav-tabs">
        <li className="nav-item">
          <Link className="navbar-link mr-3" style={isActive(history, "/")} to="/">
            Accueil
          </Link>
        </li>
        <li className="nav-item">
          <Link className="navbar-link mr-3" style={isActive(history, "/signup")} to="/signup">
            Rejoindre
          </Link>
        </li>
        <li className="nav-item">
          <Link className="navbar-link mr-3" style={isActive(history, "/signin")} to="/signin">
            Connexion
          </Link>
        </li>
        <li className="nav-item">
          <Link className="navbar-link mr-3" style={isActive(history, "/signout")} to="/signout">
            DÃ©connexion
          </Link>
        </li>
      </ul>
      <form className="form-inline my-2 my-lg-0">
        <input
          className="form-control mr-sm-2"
          type="search"
          placeholder="Search"
          aria-label="Search"
        />
        <button className="btn btn-outline-success my-2 my-sm-0" type="submit">
          Chercher un pirate
        </button>
      </form>
    </nav>
  </div>
);

export default withRouter(Menu);
