// Native modules import
import React, { Component } from "react";
import { Redirect } from "react-router-dom";

// Personal modules import
import {isAuthenticated} from '../auth/index';

class Profile extends Component {
  constructor() {
    super();
    this.state = {
      user: "",
      redirectionSignIn: false,
    };
  }

  read = async (userId, token) => {
    try {
      const response = await fetch(
        `${process.env.REACT_APP_API_URI}/user/${userId}`,
        {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        }
      });
      if (response) {
        return response.json();
      }
    } catch (error) {
      console.error('Something went wrong with read method.')
    }
  };

  init = async (userId) => {
    try {
      const token = isAuthenticated().token;
      if (token) {
        const data = await this.read(userId, token);
        if (data) {
          if (data.error) {
            this.setState = {
              redirectionSignIn: true,
            };
          }
          else {
            this.setState({
              user: data,
            });
          }
        }
      } else {
        console.error(`Token inside init method is ${typeof token}.`)
      }
    } catch (error) {
      console.error('Something went wrong with init method.')
    }
  };

  componentDidMount() {
    try {
      const userId =  this.props.match.params.userId;
      if (userId) {
        this.init(userId);
      } else {
        console.error('No userId found.')
      }
    } catch (error) {
      console.error('Something went wrong with componentDidMount method.')
    }
  };

  render() {
    const redirectionSignIn = this.state.redirectionSignIn;
    if (redirectionSignIn) {
      return <Redirect to="/signin" />
    }
    return (
      <div className="jumbotron">
        <h2 className="mb-3 mt-3">Planque</h2>
        <p className="mt-2 mb-2 lead">Bienvenue dans ta planque, {isAuthenticated().user.pseudo} !</p>
        <p>{`Tu as rejoint la Taverne des Soiffards le ${new Date(this.state.user.created).toLocaleDateString()}`}</p>
      </div>
    );
  }
}

export default Profile;
