// Native modules import
import React, { Component } from "react";
import { Redirect } from "react-router-dom";

// Personal modules import
import {isAuthenticated} from '../auth/index';

class Profile extends Component {
  constructor() {
    super();
    this.state = {
      user: "",
      redirectionSignIn: false,
    };
  }

  read = async (userId, token) => {
    console.log(`this inside read method: ${JSON.parse(this)}`)
    const response = await fetch(
      `${process.env.REACT_APP_API_URI}/user/${userId}`,
      {
      method: "GET",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`
      }
    });
    return response.json();
  };

  init = async (userId) => {
    const token = isAuthenticated().token;
    console.log(`this inside init method: ${JSON.parse(this)}`);
    const data = await this.read(userId, token);
    if (data.error) {
      this.setState = {
        redirectionSignIn: true,
      };
    }
    else {
      this.setState({
        user: data,
      });
    }
  };

  componentDidMount() {
    const userId =  this.props.match.params.userId;
    console.log(`this inside componentDidMount method: ${JSON.parse(this)}`)
    this.init(userId);
  };

  render() {
    const redirectionSignIn = this.state.redirectionSignIn;
    if (redirectionSignIn) {
      return <Redirect to="/signin" />
    }
    return (
      <div className="jumbotron">
        <h2 className="mb-3 mt-3">Planque</h2>
        <p className="mt-2 mb-2 lead">Bienvenue dans ta planque, {isAuthenticated().user.pseudo} !</p>
        <p>{`Tu as rejoint la Taverne des Soiffards le ${new Date(this.state.user.created).toLocaleDateString()}`}</p>
      </div>
    );
  }
}

export default Profile;
