// Native modules import
import React, { Component } from "react";

// Personal modules import
import {isAuthenticated} from '../auth/index';

class Profile extends Component {
  constructor() {
    super();
    this.state = {
      user: "",
      redirectionSignIn: false,
    };
  }

  componentDidMount = async () => {
    const userId =  this.props.match.params.userId;
    const token = isAuthenticated().token;
    const response = await fetch(
      `${process.env.REACT_APP_API_URI}/user/${userId}`,
      {
      method: "GET",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`
      }
    });
    const data = await response.json();
    if (data.error) {
      console.log("ERROR");
    }
    else {
      this.setState({
        user: data,
      });
    }
  };

  render() {
    return (
      <div className="jumbotron">
        <h2 className="mb-3 mt-3">Planque</h2>
        <p className="mt-2 mb-2 lead">Bienvenue dans ta planque, {isAuthenticated().user.pseudo} !</p>
        <p>{`Tu as rejoint la Taverne des Soiffards le ${new Date(this.state.user.created).toLocaleDateString()}`}</p>
      </div>
    );
  }
}

export default Profile;
