// Native modules import
import React, { Component } from "react";
import { Link } from "react-router-dom";

// Personal modules import
import { writeComment, deleteComment } from "../../logic/post/apiPost";
import { isLoggedIn } from "../../logic/auth/index";

class WriteComment extends Component {
  state = {
    message: "",
    error: "",
  };

  handleChange = (event) => {
    this.setState({
      error: "",
    });
    this.setState({
      message: event.target.value,
    });
  };

  isValid = () => {
    const { message } = this.state;
    if (!message.length > 0 || message.length > 300) {
      this.setState({
        error:
          "Ton commentaire doit être d'au moins 1 caractère et d'au plus 300 caractères, moussaillon !",
      });
      return false;
    }
    return true;
  };

  addComment = async (event) => {
    event.preventDefault();
    const userId = isLoggedIn().user._id;
    const token = isLoggedIn().token;
    const postId = this.props.postId;
    const comment = { message: this.state.message };

    if (this.isValid()) {
      try {
        const response = await writeComment(userId, token, postId, comment);
        if (response.error) {
          console.log(`[front-end/src/components/post/WriteComment.jsx => addComment] : response.error: ${response.error}`);
        } else {
          this.setState({
            message: "",
          });
          this.props.updateComments(response.comments);
        }
      } catch (error) {
        console.error(`[front-end/src/components/post/WriteComment.jsx => addComment] : error: ${error}`);
      }
    } else {
      console.error(`[front-end/src/components/post/WriteComment.jsx => addComment] : The validation didn't go through.`)
    }
  };

  // removeComment = (comment) => {
  //   const userId = isLoggedIn().user._id;
  //   const token = isLoggedIn().token;
  //   const postId = this.props.postId;

  //   deleteComment(userId, token, postId, comment).then(data => {
  //     if (data.error) {
  //         console.log(data.error);
  //     } else {
  //         this.props.updateComments(data.comments);
  //     }
  //   });
  // };

//   deleteConfirmation = comment => {
//     let answer = window.confirm(
//       "Es-tu sûr de vouloir supprimer ton message, pirate ?"
//     );
//     if (answer) {
//       this.deleteComment(comment);
//     }
// };

  render() {
    return (
      <div>
        <h2>Donne ton avis sur ce message, moussaillon !</h2>
        <div className="form-group">
          <form action="" onSubmit={this.addComment}>
            <input
              className="form-control"
              type="text"
              onChange={this.handleChange}
              placeholder="Ecris ici ton avis..."
            />
          </form>
        </div>
      </div>
    );
  }
}

export default WriteComment;
