// Native modules import
import React, { Component } from "react";

class Signin extends Component {
  constructor() {
    super();
    this.state = {
      email: "",
      password: "",
      error: "",
      redirection: false,
    };
  }

  handleChange = (name) => (event) => {
    this.setState({
      error: "",
    });
    this.setState({
      [name]: event.target.value,
    });
  };

  signinForm = (email, password) => {
    return (
      <form action="">
        <div className="form-group">
          <label htmlFor="Email" className="text-muted">
            E-mail
          </label>
          <input
            onChange={this.handleChange("email")}
            type="email"
            className="form-control"
            value={email}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="Password" className="text-muted">
            Mot de passe
          </label>
          <input
            onChange={this.handleChange("password")}
            type="password"
            className="form-control"
            value={password}
            required
          />
        </div>
        <button
          onClick={this.signinSubmit}
          type="submit"
          className="btn btn-raised btn-primary"
        >
          Entrer dans la Taverne
        </button>
      </form>
    );
  };

  authenticateUser(jwt, cb) {
    if (typeof window !== "undefined") {
      localStorage.setItem("jwt", JSON.stringify(jwt))
    }
    cb();
  };

  signinSubmit = async (event) => {
    event.preventDefault();
    const { email, password } = this.state;
    const user = { email, password };
    if ("" !== email && "" !== password) {
      const response = await this.signin(user);
      if (response.error) {
        this.setState({
          error: response.error,
        });
      } else {
        this.authenticateUser(response, () => {
          this.setState({
            redirection: true,
          })
        })

      }
    } else {
      this.setState({
        error: `Tu dois renseigner un e-mail et un mot de passe, sacrebleu !`
      })
    }
  };

  signin = async (user) => {
    try {
      const res = await fetch("http://localhost:9092/signin", {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify(user),
      });
      return await res.json();
    } catch (err) {
      console.log(err);
    }
  };

  render() {
    const { email, password, error } = this.state;
    return (
      <div className="container">
        <h2 className="mt-5 mb-5">Bienvenue, moussaillon !</h2>

        <div
          className="alert alert-warning"
          style={{ display: error ? "" : "none" }}
        >
          {error}
        </div>
        {this.signinForm(email, password)}
      </div>
    );
  }
}

export default Signin;
