//     state = {};
//     render() {
//       return (
//        <div className="container-fluid">
//           <div className="jumbotron">
//           <h2 className="mb-3 mt-3">Accueil</h2>
//           <p className="lead">Bienvenue dans la Taverne !</p>
//         </div>
//        </div>
//       )
//     }
//   }
// };

export default Home;
