        <div className="messages-container">
          <ol className="messages-list">
            {messages.map((message, i) => (
              <li
                key={i}
                className={`message-item ${
                  message.ownedByCurrentUser ? isLoggedIn().user.pseudo : "received-message"
                }`}
              >
                {message.body}
