// Native modules import
import React, { Component } from "react";
import { Link } from "react-router-dom";

// Personal modules import
import { listAllPosts } from "./apiPost";
// import defaultProfilePic from "../images/default-image.png";

class Posts extends Component {
  constructor() {
    super();
    this.state = {
      posts: [],
    };
  }

  componentDidMount = async () => {
    try {
      const allPosts = await listAllPosts();
      // console.log("All posts from server", allPosts);
      if (allPosts) {
        this.setState({
          posts: allPosts,
        });
      } else {
        console.error(
          `No posts were retrieved from the server because of error.`
        );
      }
    } catch (error) {
      console.error(
        `Couldn't change state of posts because of error: ${error}.`
      );
    }
  };

  // componentDidMount = async () => {
  //   let allPosts;
  //   try {
  //     allPosts = await listAllPosts();
  //   } catch (error) {
  //     console.error(
  //       `The list of all posts couldn't be retrieved because of error: ${error}.`
  //     );
  //   }
  //   if (allPosts) {
  //     try {
  //       this.setState({
  //         posts: allPosts,
  //       });
  //     } catch (error) {
  //       console.error(
  //         `Couldn't change state of posts because of error: ${error}.`
  //       );
  //     }
  //   } else {
  //     console.error(
  //       `No posts were retrieved from the server because of error.`
  //     );
  //   }
  // };

  renderPosts = (posts) => {
    return (
      <div className="row">
        {posts.map((post, i) => {
          const posterId = post.author ? `/user/${post.author._id}` : "";
          const posterPseudo = post.author ? post.author.pseudo : "un Inconnu";
          return (
            <div
              className="card col-xs-12 col-sm-6 col-md-4 col-lg-3 col-xl-2"
              key={i}
            >
              <div className="card-body" key={i}>
                <h5 className="card-title">{post.title}</h5>
                <p className="card-text">{post.body.substring(0, 100)}</p>
                <br />
                <p className="font-italic mark">
                  Post√© par <Link to={posterId}>{posterPseudo}</Link> le {new Date(post.created).toLocaleDateString()}
                </p>
              </div>

              <Link
                to={`/user/${posterId}`}
                className="btn btn-raised btn-sm"
                style={{ color: "#5E8C5D" }}
              >
                Lire plus
              </Link>
            </div>
          );
        })}
      </div>
    );
  };

  render() {
    const { posts } = this.state;
    return (
      <div className="jumbotron">
        <h2 className="mt-3 mb-3">Les messages de la Taverne</h2>
        {this.renderPosts(posts)}
      </div>
    );
  }
}

export default Posts;
