// Native modules import
import React, { Component } from "react";

class Signup extends Component {
  constructor() {
    super();
    this.state = {
      pseudo: "",
      email: "",
      password: "",
      error: "",
    };
  }

  handleChange = (name) => (event) => {
    this.setState({
      [name]: event.target.value,
    });
  };

  render() {
    return (
      <div className="container">
        <h2 className="mt-5 mb-5">Inscription</h2>
        <form action="">
          <div className="form-group">
            <label htmlFor="Name" className="text-muted">
              Pseudo
            </label>
            <input
              onChange={this.handleChange("pseudo")}
              type="text"
              className="form-control"
              value={this.state.name}
              placeholder="Le nom qui sera visible par les autres utilisateurs de la Taverne"
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="Email" className="text-muted">
              E-mail
            </label>
            <input
              onChange={this.handleChange("email")}
              type="email"
              className="form-control"
              value={this.state.name}
              placeholder="Votre e-mail de contact (example : guy-tard@gmail.com)"
              required
            />
            <small id="emailHelp" class="form-text text-muted">Votre e-mail ne sera partagé avec aucune tierse partie</small>
          </div>
          <div className="form-group">
            <label htmlFor="Password" className="text-muted">
              Mot de passe
            </label>
            <input
              onChange={this.handleChange("password")}
              type="password"
              className="form-control"
              value={this.state.name}
              placeholder="Ne communiquez jamais votre mot de passe à qui que ce soit."
              required
            />
          </div>
          <button type="submit" className="btn btn-raised btn-primary">
            S'inscrire
          </button>
        </form>
      </div>
    );
  }
}

export default Signup;
