// Native modules import
import React, { Component } from "react";
import { Link } from "react-router-dom";

// Personal modules import
import { viewPost } from "./apiPost";
import defaultPostPic from "../images/beautiful-sea.jpg";

class OnePost extends Component {
  state = {
    post: "",
  };

  componentDidMount = async () => {
    const postId = this.props.match.params.postId;
    const onePost = await viewPost(postId);
    // console.log('one post', onePost);
    if (onePost.error) {
      console.log(`An error happened when getting the post information.`);
    } else {
      this.setState({
        post: onePost,
      });
    }
  };

  renderPost = (post) => {
    const posterId = post.author ? `/user/${post.author._id}` : "";
    const posterPseudo = post.author ? post.author.pseudo : "un Inconnu";
    return (
      <div className="card col-12">
        <div className="card-body">
          <img
            src={`${process.env.REACT_APP_API_URI}/post/photo/${post._id}`}
            alt={post.title}
            onError={(img) => (img.target.src = `${defaultPostPic}`)}
            className="img-thumbnail mb-3"
            style={{ height: "200px", width: "auto" }}
          />
          <p className="card-text" style={{padding: "20px"}}>{post.body}</p>
          <br />
          <p className="font-italic mark">
            Post√© par <Link to={posterId}>{posterPseudo}</Link> le{" "}
            {new Date(post.created).toLocaleDateString()}
          </p>
        </div>

        <Link
          to={`/posts`}
          className="btn btn-raised btn-sm"
          style={{ color: "#5E8C5D" }}
        >
          Retour aux messages
        </Link>
      </div>
    );
  };

  render() {
    const { post } = this.state;
    return (
      <div className="jumbotron">
        <h2>{post.title}</h2>
        <div className="container">{this.renderPost(post)}</div>
      </div>
    );
  }
}

export default OnePost;
