// Navite modules import
import React, { Component } from "react";

// Personal modules import
import { isLoggedIn } from "../auth/index";
import { read } from './apiUser';

class EditProfile extends Component {
  constructor() {
    super()
    this.state = {
      id: "",
      pseudo: "",
      email: "",
      password: "",
    }
  }

  init = async (userId) => {
    let token;
    try {
      token = isLoggedIn().token;
    } catch (error) {
      console.error(`Couldn't retrieve token because of error: ${error}.`);
    }
    if (token) {
      let data;
      try {
        data = await read(userId, token);
      } catch (error) {
        console.error(`Couldn't retrieve data because of error: ${error}.`);
      }
      if (data) {
        try {
          if (data.error) {
            try {
              this.setState = {
                redirectionSignIn: true,
              };
            } catch (error) {
              console.error(`Couldn't set state of redirectionSignIn.`);
            }
          } else {
            try {
              this.setState({
                id: data._id,
                pseudo: data.pseudo,
                email: data.email,
              });
            } catch (error) {
              console.error(`Couldn't set state or of user.`);
            }
          }
        } catch (error) {
          console.error(
            `No error property inside data object was found because of error: ${error}.`
          );
        }
      } else {
        console.error(`No data object was found.`);
      }
    } else {
      console.error(`Token inside init method is ${typeof token}.`);
    }
  };

  handleChange = (name) => (event) => {
    this.setState({
      [name]: event.target.value,
    });
  };

  updateSubmit = async (event) => {
    event.preventDefault();
    const { pseudo, email, password } = this.state;
    const newUser = { pseudo, email, password };
    let response;
    console.log(newUser);
    // try {
    //   response = await signup(newUser);
    // } catch (error) {
    //   console.error(
    //     `Couldn't get a response, type of response is ${typeof response}.`
    //   );
    // }
    // // if (response.error) {
    //   try {
    //     this.setState({
    //       error: response.error,
    //     });
    //   } catch (error) {
    //     console.error(`Couldn't change state because of error: ${error}.`);
    //   }
    // } else {
    //   try {
    //     this.setState({
    //       pseudo: "",
    //       email: "",
    //       password: "",
    //       error: "",
    //       success: true,
    //     });
    //     // setTimeout(() => {
    //     //   this.setState({
    //     //     success: false,
    //     //   });
    //     // }, 3000);
    //   } catch (error) {
    //     console.error(`Couldn't change state because of error: ${error}.`);
    //   }
    // }
  };

  updateForm = (pseudo, email, password) => {
    return (
      <form action="">
        <div className="form-group">
          <label htmlFor="Name" className="text-muted">
            Pseudo
          </label>
          <input
            onChange={this.handleChange("pseudo")}
            type="text"
            className="form-control"
            value={pseudo}
            placeholder="Le nom qui sera visible par les autres utilisateurs de la Taverne"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="Email" className="text-muted">
            E-mail
          </label>
          <input
            onChange={this.handleChange("email")}
            type="email"
            className="form-control"
            value={email}
            placeholder="Votre e-mail de contact (example : guy-tard@gmail.com)"
            required
          />
          <small id="emailHelp" className="form-text text-muted">
            Votre e-mail ne sera partagé avec aucune tierse partie
          </small>
        </div>
        <div className="form-group">
          <label htmlFor="Password" className="text-muted">
            Mot de passe
          </label>
          <input
            onChange={this.handleChange("password")}
            type="password"
            className="form-control"
            value={password}
            placeholder="Ne communiquez jamais votre mot de passe à qui que ce soit."
            required
          />
        </div>
        <button
          onClick={this.updateSubmit}
          type="submit"
          className="btn btn-raised btn-primary"
        >
          Modifier
        </button>
      </form>
    );
  };

  render() {
    const {pseudo, email, password} = this.state;
    return (
      <div className="container">
        <h2 className="mt-5 mb-5">Edit Profile</h2>
        {this.updateForm(pseudo, email, password)}
      </div>
    );
  }

  componentDidMount() {
    try {
      const userId = this.props.match.params.userId;
      if (userId) {
        this.init(userId);
      } else {
        console.error("No userId found.");
      }
    } catch (error) {
      console.error("Something went wrong with componentDidMount method.");
    }
  }
}

export default EditProfile;
