// Native modules import
import React, { Component } from "react";
import { Link } from "react-router-dom";

// Personal modules import
import { listAllUsers } from "./apiUser";
import defaultProfilePic from "../images/default-image.png";

class Users extends Component {
  state = {
    users: [],
  };

  componentDidMount = async () => {
    try {
      const allUsers = await listAllUsers();
      if (allUsers) {
        this.setState({
          users: allUsers,
        });
        // console.log('all users: ', allUsers);
      } else {
        console.error(
          `No users were retrieved from the server because of error.`
        );
      }
    } catch (error) {
      console.error(`Couldn't list all users because of error: ${error}.`);
    }
  };

  renderUsers = (users) => (
    <div
      className="row"
      style={{ justifyContent: "center", position: "relative" }}
    >
      {users.map((user, i) => {
        const { photo, _id, pseudo, about } = user;
        if (photo) {
          return (
            <div
              className="card card-block col-sm-12 col-md-6 col-lg-4 col-xl-3"
              key={i}
              style={{
                backgroundColor: "#D5E5F2",
                marginRight: "30px",
                marginBottom: "30px",
                boxShadow: "3px 3px 5px grey",
              }}
            >
              <img
                style={{
                  height: "auto",
                  width: "auto",
                  marginTop: "15px",
                  borderRadius: "25%",
                }}
                src={`${process.env.REACT_APP_API_URI}/user/photo/${_id}`}
                onError={(img) => (img.target.src = `${defaultProfilePic}`)}
                className="img-thumbnail mb-3"
                alt={pseudo}
              />
              <div
                className="card-body d-flex flex-column"
                // className="card-body col"
                key={i}
                // style={{ dispaly: "flex", alignItems: "center", justifyContent: "center" }}
              >
                <div className="row">
                  <h5 className="card-title text-center" style={{ fontWeight: "bold", alignItems: "center", justifyContent: "center" }}>
                    {pseudo}
                  </h5>
                  <p className="card-text lead">{about}</p>
                </div>
                <div className="row">
                  <Link
                    to={`/user/${_id}`}
                    className="d-flex align-self-end btn btn-lg btn-block btn-outline-success"
                    // className="btn btn-raised btn-block btn-primary text-center btn-lg align-self-end"
                    // style={{
                    //   fontWeight: "bold",
                    //   fontSize: "0.9rem",
                    //   marginBottom: "15px",
                    //   boxShadow: "3px 3px 5px grey",
                    //   backgroundColor: "#81A65D",
                    //   justifyContent: "center",
                    //   alignItems: "center"
                    // }}
                  >
                    Visiter sa planque
                  </Link>
                </div>
              </div>
            </div>
          );
        } else {
          return (
            <div
              className="card card-block col-sm-12 col-md-6 col-lg-4 col-xl-3"
              key={i}
              style={{
                backgroundColor: "#D5E5F2",
                marginRight: "30px",
                marginBottom: "30px",
              }}
            >
              <img
                style={{
                  height: "auto",
                  width: "auto",
                  marginTop: "15px",
                  borderRadius: "50%",
                }}
                src={`${defaultProfilePic}`}
                onError={(img) => (img.target.src = `${defaultProfilePic}`)}
                className="img-thumbnail mb-3"
                alt={pseudo}
              />
              <div className="card-body d-flex flex-column" key={i}>
                <h5 className="card-title text-center">{pseudo}</h5>
                <p className="card-text lead mb-3">{about}</p>
                <button className="align-self-end">
                {/* <Link
                  to={`/user/${_id}`}
                  // className="btn btn-lg btn-block btn-outline-primary text-center"
                  // style={{
                  //   fontWeight: "bold",
                  //   fontSize: "0.9rem",
                  //   marginBottom: "15px",
                  // }}
                >
                  Visiter sa planque
                </Link> */}
                </button>
              </div>
            </div>
          );
        }
      })}
    </div>
  );

  render() {
    const { users } = this.state;
    return (
      <div className="jumbotron">
        <h2 className="mt-3 mb-3 text-center" style={{ fontWeight: "bold" }}>
          Les pirates de la Taverne
        </h2>
        {this.renderUsers(users)}
      </div>
    );
  }
}

export default Users;
