export const createPost = async (userId, token, post) => {
  let response;
  try {
    response = await fetch(`${process.env.REACT_APP_API_URI}/post/new/${userId}`, {
      method: "POST",
      headers: {
        Accept: "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: post,
    });
  } catch (error) {
    return console.error(
      `Couldn't post data to api because of error: ${error}.`
    );
  }
  if (response) {
    try {
      return await response.json();
    } catch (error) {
      return console.error(
        `Couldn't get the response.json() because of error: ${error}.`
      );
    }
  } else {
    return console.error(`Couldn't find any response for some reason...`);
  }
};
