// Native modules import
import React, { Component } from "react";
import { Redirect, Link } from "react-router-dom";

// Personal modules import
import { isLoggedIn } from "../auth/index";
import { read } from "./apiUser";
import defaultProfilePic from "../images/default-image.png";

class Profile extends Component {
  constructor() {
    super();
    this.state = {
      user: "",
      redirectionSignIn: false,
    };
  }

  init = async (userId) => {
    let token;
    try {
      token = isLoggedIn().token;
    } catch (error) {
      console.error(`Couldn't retrieve token because of error: ${error}.`);
    }
    if (token) {
      let data;
      try {
        data = await read(userId, token);
      } catch (error) {
        console.error(`Couldn't retrieve data because of error: ${error}.`);
      }
      if (data) {
        try {
          if (data.error) {
            try {
              this.setState = {
                redirectionSignIn: true,
              };
            } catch (error) {
              console.error(`Couldn't set state of redirectionSignIn.`);
            }
          } else {
            try {
              this.setState({
                user: data,
              });
            } catch (error) {
              console.error(`Couldn't set state or of user.`);
            }
          }
        } catch (error) {
          console.error(
            `No error property inside data object was found because of error: ${error}.`
          );
        }
      } else {
        console.error(`No data object was found.`);
      }
    } else {
      console.error(`Token inside init method is ${typeof token}.`);
    }
  };

  componentDidMount() {
    try {
      const userId = this.props.match.params.userId;
      if (userId) {
        this.init(userId);
      } else {
        console.error("No userId found.");
      }
    } catch (error) {
      console.error("Something went wrong with componentDidMount method.");
    }
  }

  render() {
    const { redirectionSignIn, user } = this.state;
    if (redirectionSignIn) {
      return <Redirect to="/signin" />;
    }
    return (
      <div className="container">
        <div className="row">
          <div className="col-md-6 jumbotron lead">
            <h2 className="mb-3 mt-3">Planque</h2>
            <img
              className="card-img-top"
              src={defaultProfilePic}
              style={{ width: "50%", objectFit: "cover" }}
              alt={user.pseudo}
            />
            <p className="mt-2 mb-2 lead">
              Bienvenue dans la planque
              {/* {/^[aeiou]/i.test(user.pseudo.toLowerCase()) ? "d'" : "de "} */}
              {user.pseudo} !
            </p>
            <p>{`Inscription à la Taverne des Soiffards le ${new Date(
              user.created
            ).toLocaleDateString()}`}</p>
            <p>{`Adresse e-mail : ${user.email}`}</p>
          </div>

          <div className="col-md-6 jumbotron">
            {isLoggedIn().user && isLoggedIn().user._id === user._id && (
              <div className="d-inline-block mt-5">
                <Link
                  to={`/user/edit/${user._id}`}
                  className="btn btn-raised btn-success mr-5"
                >
                  Modifier le profil
                </Link>
                <button className="btn btn-raised btn-danger">
                  Supprimer le profil
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }
}

export default Profile;
