/* eslint-disable no-lone-blocks */
// Native modules import
import React from "react";

// Personal modules import
import { isLoggedIn } from "../auth/index";
import SoTWP from "../images/sot-wp.jpg";

// eslint-disable-next-line no-empty-pattern
const Taverne = ({}) => {
  if (isLoggedIn()) {
    return (
      <div className="container jumbotron">
        <div className="row">
          <div className="col-md-12 mt-5 mb-5">
            <h4 className=" text-center" style={{ fontWeight: "bold" }}>
              Bienvenue dans la Taverne, {isLoggedIn().user.pseudo}
            </h4>
            <img
              src={`${SoTWP}`}
              alt="wall-paper-sea-of-thieves"
              style={{
                height: "auto",
                width: "auto",
                maxHeight: "auto",
                maxWidth: "auto",
                objectFit: "contain",
                boxShadow: "3px 3px 10px grey",
              }}
            />
          </div>
        </div>
      </div>
    );
  } else {
    return (
      <div className="container jumbotron">
        <div className="row">
          <div className="col-md-12 mt-5 mb-5">
            <h4 className=" text-center" style={{ fontWeight: "bold" }}>
              Bienvenue dans la Taverne, pirate !
            </h4>
            <img src={`${SoTWP}`} alt="wall-paper-sea-of-thieves" />
          </div>
        </div>
      </div>
    );
  }
};

export default Taverne;
