// Native modules import
import React, { Component } from 'react'

// Personal modules import
// import {isLoggedIn} from '../auth/index';
import {viewPost} from './apiPost';

class EditPost extends Component {
  constructor() {
    super()
    this.state = {
      id: "",
      title:"",
      body:"",
      redirectionProfile: false,
    }
  }
 
  init = async (userId) => {
    const data = await viewPost(userId);
    if (data.error) {
      this.setState = {
        redirectionProfile: true,
      };
    } else {
      this.setState({
        id: data._id,
        title: data.title,
        body: data.body,
        about: data.about || "",
        error: "",
      });
    }
  };

  componentDidMount() {
    this.postData = new FormData();
    try {
      const postId = this.props.match.params.postId;
      if (postId) {
        this.init(postId);
      } else {
        console.error("No postId found.");
      }
    } catch (error) {
      console.error("Something went wrong with componentDidMount method.");
    }
  }

  render() { 
    return ( 
      <div>
        <h2>Edit post</h2>
        {this.props.match.params.postId}
      </div>
     );
  }
}
 
export default EditPost;
