// Native modules import
import React, { Component } from "react";
import { Redirect } from "react-router-dom";

// Personal modules import
// import {isLoggedIn} from '../auth/index';
import { viewPost } from "./apiPost";
import defaultPostPic from "../images/beautiful-sea.jpg";
import { isLoggedIn } from "../auth/index";
import { updatePost } from "../post/apiPost";

class EditPost extends Component {
  constructor() {
    super();
    this.state = {
      id: "",
      title: "",
      body: "",
      redirectionProfile: false,
      error: "",
      fileSize: 0,
      loading: false,
    };
  }

  init = async (postId) => {
    try {
      const data = await viewPost(postId);
      if (data.error) {
        this.setState({
          redirectionProfile: true,
        });
      } else {
        this.setState({
          id: data.author._id,
          title: data.title,
          body: data.body,
          error: "",
        });
      }
    } catch (error) {
      console.error(
        `The init method encountered the following error: ${error}`
      );
    }
  };

  componentDidMount() {
    this.postData = new FormData();
    try {
      const postId = this.props.match.params.postId;
      if (postId) {
        this.init(postId);
      } else {
        console.error("No postId found.");
      }
    } catch (error) {
      console.error("Something went wrong with componentDidMount method.");
    }
  }

  isValid = () => {
    const { title, body, fileSize } = this.state;
    if (fileSize > 1000000) {
      this.setState({
        loading: false,
        error: "Le poids du fichier ne doit pas dépasser 1Mb ! Déconne pas !",
      });
      return false;
    }
    if (title.length === 0 || title.length > 50) {
      this.setState({
        loading: false,
        error:
          "Le titre est requis et doit faire moins de 50 caractères, marin d'eau douce !",
      });
      return false;
    }
    if (body.length === 0) {
      this.setState({
        loading: false,
        error: `Un message est requis pour envoyer un... message, moussaillon.`,
      });
      return false;
    }
    return true;
  };

  // handleChange = (name) => (event) => {
  //   this.setState({ error: "" });
  //   const value = name === "photo" ? event.target.files[0] : event.target.value;
  //   const fileSize = name === "photo" ? event.target.files[0].size : 0;
  //   this.postData.set(name, value);
  //   this.setState({
  //     [name]: value,
  //     fileSize,
  //   });
  // };

  handleChange = (name) => (event) => {
    this.setState({ error: "" });
    const value = name === "photo" ? event.target.files[0] : event.target.value;

    const fileSize = name === "photo" ? event.target.files[0].size : 0;
    this.postData.set(name, value);
    this.setState({ [name]: value, fileSize });
  };

  submitUpdate = async (event) => {
    event.preventDefault();
    this.setState({ loading: true });

    if (this.isValid()) {
      const postId = this.props.match.params.postId;
      const token = isLoggedIn().token;

      const data = await updatePost(postId, token, this.postData);
      if (data.error) {
        this.setState({ error: data.error });
      } else {
        this.setState({
          loading: false,
          title: "",
          body: "",
          redirectionProfile: true,
        });
      }
    }
  };

  postUpdate = (title, body) => (
    <form action="">
      <div className="form-group">
        <label className="text-muted">Image</label>
        <input
          onChange={this.handleChange("photo")}
          type="file"
          accept="image/*"
          className="form-control"
        />
      </div>
      <div className="form-group">
        <label className="text-muted">Titre</label>
        <input
          onChange={this.handleChange("title")}
          type="text"
          className="form-control"
          value={title}
        />
      </div>
      <div className="form-group">
        <label className="text-muted">Message</label>
        <textarea
          onChange={this.handleChange("body")}
          type="body"
          className="form-control"
          value={body}
        />
      </div>
      <button
        onClick={this.submitUpdate}
        type="submit"
        className="btn btn-raised btn-primary"
      >
        Envoyer
      </button>
    </form>
  );

  render() {
    const { title, body, id, redirectionProfile } = this.state;
    console.log('id inside render: ', id)
    console.log('user._id inside render: ', isLoggedIn().user._id)
    console.log('user inside render: ', isLoggedIn().user)
    if (redirectionProfile) {
      <Redirect to={`/user/${isLoggedIn().user._id}`} />;
    }
    return (
      <div>
        <div className="card-body">
          <img
            src={`${process.env.REACT_APP_API_URI}/post/photo/6015ffadda6a195de889a999}`}
            alt={title}
            onError={(img) => (img.target.src = `${defaultPostPic}`)}
            className="img-thumbnail mb-3"
            style={{ height: "200px", width: "auto" }}
          />

          <h3 className="card-title mt-3 mb-3">{title}</h3>

          {isLoggedIn().user._id === id && this.postUpdate(title, body)}
        </div>
      </div>
    );
  }
}

export default EditPost;

