/* eslint-disable jsx-a11y/anchor-is-valid */
// Native modules import
import React from "react";
import { Link, withRouter } from "react-router-dom";

// Personal modules import
import { signout, isLoggedIn } from "../auth/index";

const isActive = (history, path) => {
  if (history.location.pathname === path) {
    return {
      color: "#5E8C5D",
    };
  } else {
    return {
      color: "#0D0D0D",
    };
  }
};

const Menu = ({ history }) => (
  <div className="container jumbotron">
    <nav className="navbar navbar-light bg-light" style={{ margin: 0 }}>
      {/* <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
        <span className="navbar-toggler-icon"></span>
      </button> */}
      <ul className="nav nav-tabs">
        <li className="nav-item">
          <Link
            className="navbar-link mr-3"
            style={(isActive(history, "/"), { fontWeight: "bold" })}
            to="/"
          >
            La Taverne
          </Link>
        </li>

        <li className="nav-item">
          <Link
            className="navbar-link mr-3"
            style={isActive(history, "/users")}
            to="/users"
          >
            Les Pirates de la Taverne
          </Link>
        </li>

        {!isLoggedIn() && (
          <>
            <li className="nav-item">
              <Link
                className="navbar-link mr-3"
                style={isActive(history, "/signin")}
                to="/signin"
              >
                Connexion
              </Link>
            </li>

            <li className="nav-item">
              <Link
                className="navbar-link mr-3"
                style={isActive(history, "/signup")}
                to="/signup"
              >
                Rejoindre
              </Link>
            </li>
          </>
        )}

        {isLoggedIn() && (
          <>
            <li className="nav-item">
              <Link
                to="/posts"
                style={isActive(history, `/posts`)}
                className="navbar-link mr-3"
              >
                Messages
              </Link>
            </li>

            <li className="nav-item">
              <Link
                to="/post/create"
                style={isActive(history, `/post/create`)}
                className="navbar-link mr-3"
              >
                Envoyer un message
              </Link>
            </li>

            <li className="nav-item">
              <Link
                className="navbar-link mr-3"
                style={
                  (isActive(history, `/user/${isLoggedIn().user._id}`),
                  { fontWeight: "bold", color: "#81A65D" })
                }
                to={`/user/${isLoggedIn().user._id}`}
              >
                {`Planque ${
                  /^[aeiou]/i.test(isLoggedIn().user.pseudo.toLowerCase())
                    ? "d'"
                    : "de "
                }${isLoggedIn().user.pseudo}`}
              </Link>
            </li>

            <li className="nav-item">
              <Link
                to="/"
                onClick={() => signout(() => history.push("/"))}
                className="navbar-link mr-5"
                style={
                  (isActive(history, "/signout"),
                  { color: "#8C0303", fontWeight: "bold" })
                }
              >
                Déconnexion
              </Link>
            </li>
            {isLoggedIn() && isLoggedIn().user.right === "Roi des Pirates" && (
              <li className="nav-item">
                <Link
                  to="/admin"
                  className="navbar-link mr-5"
                  style={
                    (isActive(history, "/admin"),
                    { color: "#9662E3", fontWeight: "bold" })
                  }
                >
                  Roi des Pirates
                </Link>
              </li>
            )}
          </>
        )}
      </ul>
      {/* <form className="form-inline my-2 my-lg-0">
        <input
          className="form-control mr-sm-2"
          type="search"
          placeholder="Search"
          aria-label="Search"
        />
        <button className="btn btn-outline-success my-2 my-sm-0" type="submit">
          Trouver un pirate
        </button>
      </form> */}
    </nav>
  </div>
);

export default withRouter(Menu);
